    public static function sendMessageAfterBonus($userID , $text)
    {
        $chats = Chats::find()->andWhere(['name' => '#admin_'.$userID])->one();
        if($chats){
            $chatMessage = new ChatMessage();
            $chatMessage->chat_id = $chats->id;
            $chatMessage->user_id = 1;
            $chatMessage->message = $text;
            $chatMessage->save();
        }
    }

