    public static function map($array, $from, $to, $group = null)
    {   
        $regions = Regions::find()->asArray()->all();
        $result = [];
        foreach ($array as $element) {
            $key = $element[$from];
            $value = $element[$to];
            if ($group !== null) {
                $result[$regions[$element[$group]-1]['name']][$key] = $value;
            } else {
                $result[$key] = $value;
            }
        }

        return $result;
    }
    public static function mapService($array, $from, $to)
    {
        $result = [];
        foreach ($array as $element) {
            $key = $element[$from];
            $value = $element[$to]." (".$element['module_title'].")";
            $result[$key] = $value;
        }

        return $result;
    }


    /**
     * statistikani foizlarda chiqarish uchun
     * @param $array
     * @param $begin
     * @param $end
     * @return array[]
     */
    public static function percent_dateBy($array, $begin, $end)
    {
        $date = $begin;

        $date = $begin;
        $label = [];
        $count = [];

        foreach ($array as $value){
            if($value['date_cr'] == $date){
                $label[] = $value['date_cr'];
                $count[] = $value['count'];
                $date = date("Y-m-d", strtotime("+1 day", strtotime($date)));
            }else{
                while(true){
                    if($value['date_cr'] < $date){
                        $label[] = $date;
                        $count[] = 0;
                        $date = date("Y-m-d", strtotime("+1 day", strtotime($date)));
                    }else{
                        $label[] = $value['date_cr'];
                        $count[] = $value['count'];
                        $date = date("Y-m-d", strtotime("+1 day", strtotime($date)));
                        break;
                    }
                }
            }
        }
        while($date < $end){
            $label[] = $date;
            $count[] = 0;
            $date = date("Y-m-d", strtotime("+1 day", strtotime($date)));
        }
        return [
            'label' => $label,
            'count' => $count
        ];
    }


    /**
     * @param $array
     * @param $begin
     * @param $end
     * @return array[]
     */
    public static function percent_MonthBy($array, $begin, $end)
    {
        $date = $begin;

        $date = $begin;
        $label = [];
        $count = [];
        while($date <= $end){
            if($key = self::searchForId($date, $array)) {
                $label[] = $array[$key]['date_cr'];
                $count[] = $array[$key]['count'];
                $date = date("Y-m", strtotime("+1 month", strtotime($date)));
            } else {
                $label[] = $date;
                $count[] = 0;
                $date = date("Y-m", strtotime("+1 month", strtotime($date)));
            }
        }
        return [
            'label' => $label,
            'count' => $count
        ];
    }


    public static function searchForId($id, $array) {
        foreach ($array as $key => $val) {
            if ($val['date_cr'] === $id) {
                return $key;
            }
        }
        return null;
    }

    public static function getPercentStatistic($array_old , $array_end)
    {
        $label = [];
        $count = [];
        $length = count($array_old['label']);
        for ($i = 0; $i < $length; $i++)
        {
            if(isset($array_old['label'][$i]) && isset($array_end['label'][$i]))
            {
               $label [] = date("d.m.Y",strtotime($array_old['label'][$i])) ." | ". date("d.m.Y",strtotime($array_end['label'][$i]));
               if($array_old['count'][$i] != 0 ){
                   $count [] = $array_end['count'][$i] != 0 ?  (int) (($array_end['count'][$i] / $array_old['count'][$i]) * 100) : 0;
               } else {
                   $count [] = $array_end['count'][$i] != 0 ? $array_end['count'][$i] : 0;
               }
            }
        }
        return [
            'label' => $label,
            'count' => $count
        ];

    }


